// src/components/EcosystemCard.js
import React from 'react';

const EcosystemCard = ({ ecosystem, onClose }) => {
  if (!ecosystem) return null;

  return (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <div className="flex justify-between items-start mb-4">
        <h2 className="text-2xl font-bold">{ecosystem.name}</h2>
        <button
          onClick={onClose}
          className="text-gray-500 hover:text-gray-700"
        >
          âœ•
        </button>
      </div>

      {ecosystem.image && (
        <img
          src={ecosystem.image}
          alt={ecosystem.name}
          className="w-full h-48 object-cover rounded-lg mb-4"
        />
      )}

      <div className="space-y-4">
        <div>
          <h3 className="font-semibold mb-2">Description</h3>
          <p>{ecosystem.description}</p>
        </div>

        <div>
          <h3 className="font-semibold mb-2">Climate</h3>
          <p>{ecosystem.climate}</p>
        </div>

        <div>
          <h3 className="font-semibold mb-2">Key Species</h3>
          <ul className="list-disc pl-5">
            {ecosystem.keySpecies?.map((species, index) => (
              <li key={index}>{species}</li>
            ))}
          </ul>
        </div>

        <div>
          <h3 className="font-semibold mb-2">Conservation Status</h3>
          <p>{ecosystem.conservationStatus}</p>
        </div>
      </div>
    </div>
  );
};

export default EcosystemCard;
