// src/components/Globe.js
'use client';
import React, { useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import EarthGlobe from './EarthGlobe';
import EcosystemCard from './EcosystemCard';
import { ecosystemData } from '../data/ecosystems';

const Globe = () => {
  const [selectedEcosystem, setSelectedEcosystem] = useState(null);

  return (
    <div className="relative w-full h-full">
      {/* Title */}
      <div className="absolute top-0 left-0 m-4 z-10">
        <h1 className="text-3xl font-bold text-white">Earth's Ecosystems</h1>
        <p className="text-white">Explore our planet's diverse environments</p>
      </div>

      {/* 3D Globe */}
      <Canvas 
        camera={{ position: [0, 0, 5], fov: 45 }}
        className="w-full h-full bg-black"
      >
        <ambientLight intensity={0.5} />
        <directionalLight position={[10, 10, 5]} intensity={1} />
        <EarthGlobe 
          onSelectEcosystem={setSelectedEcosystem}
          ecosystems={ecosystemData}
        />
        <OrbitControls 
          enablePan={false}
          enableZoom={true}
          minDistance={3}
          maxDistance={10}
        />
      </Canvas>

      {/* Ecosystem Information Card */}
      {selectedEcosystem && (
        <div className="absolute right-0 top-0 m-4 w-96 z-10">
          <EcosystemCard 
            ecosystem={selectedEcosystem}
            onClose={() => setSelectedEcosystem(null)}
          />
        </div>
      )}
    </div>
  );
};

export default Globe;
